mapper.update:
UPDATE account
SET account_number = #{accountNumber},
    bank_name = #{bankName}
WHERE user_id = #{userId}

mapper.select:
SELECT account_id, user_id, account_number, bank_name, balance
FROM account
WHERE user_id = #{userId}

mapper.update:
UPDATE account
SET balance = balance - #{amount}
WHERE user_id = #{userId}
AND balance >= #{amount}

UPDATE account
SET balance = balance + #{amount}
WHERE user_id = #{userId}

UPDATE merchant_account
SET merchant_balance = merchant_balance + #{amount}
WHERE merchant_id = #{merchantId}
