mapper.select:
SELECT password
FROM users
WHERE user_id = #{userId}

mapper.update:
UPDATE users
SET password = #{password},
    updated_at = #{updatedAt}
WHERE user_id = #{userId}

mapper.select:
SELECT COUNT(*)
FROM users
WHERE email = #{email}
  AND user_id != #{userId}

SELECT COUNT(*)
FROM users
WHERE nickname = #{nickname}
  AND user_id != #{userId}

mapper.update:
UPDATE users
SET fcm_token = #{fcmToken},
    updated_at = #{updatedAt}
WHERE user_id = #{userId}
